	state: function() {
		var currentNode = this.currentNode();

		return $(currentNode).is('aside') || $(currentNode).parents('aside').length > 0 ? 1 : 0;
	},
	exec: function () {
		this.insert('[footnote] ', '[/footnote]', false);
	},
	txtExec: ['[footnote]', '[/footnote]'],
	tooltip: 'Insert Footnote'

