	$sortFunction = empty($membersOnlineOptions['reverse_sort']) ? 'ksort' : 'krsort';

	// Sort the two lists.
	$sortFunction($membersOnlineStats['users_online']);
	$sortFunction($membersOnlineStats['list_users_online']);

