/**
 * For highest feed compatibility, some special characters should be provided 
 * as character entities and not html entities 
 *
 * @param string $data
 */
function encode_special($data)
{
	return strtr($data, array('>' => '&#x3E;', '&' => '&#x26;', '<' => '&#x3C;'));
}

