/**
 * Check if the system supports webP
 *
 * @return bool
 */
function hasWebpSupport()
{
	if (checkImagick())
	{
		$check = Imagick::queryformats();
		return in_array('WEBP', $check);
	}

	if (checkGD())
	{
		$check = gd_info();
		return !empty($check['WebP Support']);
	}

	return false;
}

